function earnedBadges(s,lang){const total=s.completedAdventures.length;const c=new Set(s.completedAdventures.map(a=>a.category));return getBadges(lang).filter(b=>{if(b.req==="streak3")return s.bestStreak>=3;if(b.req==="streak7")return s.bestStreak>=7;if(b.req==="streak14")return s.bestStreak>=14;if(b.req==="allcat")return c.size>=6;if(b.req==="location")return(s.locationAdventureCount||0)>=5;return total>=b.req})}
function timeAgo(ts,lang){if(!ts)return"";const now=Date.now();const tm=ts.toDate?ts.toDate().getTime():typeof ts==="number"?ts:Date.now();const d=Math.floor((now-tm)/1000);if(lang==="en"){if(d<60)return"Now";if(d<3600)return Math.floor(d/60)+" min ago";if(d<86400)return Math.floor(d/3600)+" hr ago";return Math.floor(d/86400)+" days ago"}if(d<60)return"≈ûimdi";if(d<3600)return Math.floor(d/60)+" dk √∂nce";if(d<86400)return Math.floor(d/3600)+" saat √∂nce";return Math.floor(d/86400)+" g√ºn √∂nce"}
