function mapLink(lat,lon,name,city){return"https://www.google.com/maps/search/"+encodeURIComponent((name||"")+(city?", "+city:""))+"/@"+lat+","+lon+",16z"}
function mapLinkName(name,city){return`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(name+(city?", "+city:""))}`}
async function fetchNearbyPlaces(lat,lon){const r=3000;const q=`[out:json][timeout:15];(node["tourism"~"museum|gallery|artwork|viewpoint|attraction|zoo|aquarium|theme_park"](around:${r},${lat},${lon});node["historic"~"monument|memorial|castle|ruins|archaeological_site|building"](around:${r},${lat},${lon});node["amenity"~"cafe|restaurant|fast_food|bar|pub|ice_cream|library|cinema|theatre|arts_centre|community_centre|marketplace|food_court"](around:${r},${lat},${lon});node["leisure"~"park|garden|playground|sports_centre|fitness_centre|swimming_pool|bowling_alley"](around:${r},${lat},${lon});node["shop"~"books|antiques|bakery|pastry|confectionery|florist|art|music|gift|clothes|mall"](around:${r},${lat},${lon});way["leisure"~"park|garden"](around:${r},${lat},${lon});way["tourism"~"museum|gallery|attraction"](around:${r},${lat},${lon});node["cuisine"](around:${r},${lat},${lon}););out body 80;`;try{const res=await fetch("https://overpass-api.de/api/interpreter",{method:"POST",body:"data="+encodeURIComponent(q)});const data=await res.json();const seen=new Set();return data.elements.filter(e=>{if(!e.tags)return false;const name=e.tags["name:tr"]||e.tags.name;if(!name||name.length<3||name.length>60)return false;if(seen.has(name.toLowerCase()))return false;seen.add(name.toLowerCase());const lt=e.lat||e.center?.lat;if(!lt)return false;return true}).map(e=>{const tags=e.tags;let type=tags.tourism||tags.historic||tags.amenity||tags.leisure||tags.shop||"place";if(tags.amenity==="cafe"||tags.amenity==="ice_cream")type="cafe";if(tags.amenity==="restaurant"||tags.amenity==="fast_food"||tags.amenity==="food_court")type="restaurant";if(tags.shop==="bakery"||tags.shop==="pastry"||tags.shop==="confectionery")type="bakery";if(tags.amenity==="bar"||tags.amenity==="pub")type="cafe";if(tags.amenity==="cinema"||tags.amenity==="theatre")type="cinema";if(tags.leisure==="fitness_centre"||tags.leisure==="sports_centre")type="gym";if(tags.shop==="books")type="books";if(tags.shop==="clothes"||tags.shop==="mall")type="shopping";if(tags.tourism==="gallery"||tags.amenity==="arts_centre")type="gallery";return{name:tags["name:tr"]||tags.name,type,lat:e.lat||e.center?.lat,lon:e.lon||e.center?.lon}}).slice(0,60)}catch(e){console.error("Overpass error:",e);return[]}}
